DROP TABLE IF EXISTS admin;
CREATE TABLE `admin` (  `id` int(5) NOT NULL AUTO_INCREMENT,  `admin` varchar(50) NOT NULL,  `pwd` varchar(20) NOT NULL,  PRIMARY KEY (`id`)) ENGINE=MyISAM AUTO_INCREMENT=2 DEFAULT CHARSET=utf8;
DROP TABLE IF EXISTS gonggao;
CREATE TABLE `gonggao` (  `id` int(5) NOT NULL AUTO_INCREMENT,  `text` text NOT NULL,  `time` varchar(50) NOT NULL,  PRIMARY KEY (`id`)) ENGINE=MyISAM AUTO_INCREMENT=21 DEFAULT CHARSET=utf8;
DROP TABLE IF EXISTS img;
CREATE TABLE `img` (  `id` int(5) NOT NULL AUTO_INCREMENT,  `src` varchar(50) NOT NULL,  `type` varchar(50) NOT NULL,  `size` varchar(50) NOT NULL,  PRIMARY KEY (`id`)) ENGINE=MyISAM AUTO_INCREMENT=45 DEFAULT CHARSET=utf8;
DROP TABLE IF EXISTS liuyanban;
CREATE TABLE `liuyanban` (  `id` int(5) NOT NULL AUTO_INCREMENT,  `ip` varchar(50) NOT NULL,  `qq` varchar(50) NOT NULL,  `time` varchar(50) NOT NULL,  `local` varchar(50) NOT NULL,  `content` text NOT NULL,  `simi` tinyint(1) NOT NULL,  `name` varchar(50) NOT NULL,  `src` varchar(100) NOT NULL,  PRIMARY KEY (`id`)) ENGINE=MyISAM AUTO_INCREMENT=215 DEFAULT CHARSET=utf8;
DROP TABLE IF EXISTS lm;
CREATE TABLE `lm` (  `id` int(5) NOT NULL AUTO_INCREMENT,  `lm1` varchar(20) NOT NULL,  `lm2` varchar(20) NOT NULL,  `lm3` varchar(20) NOT NULL,  `lmid` int(10) NOT NULL,  PRIMARY KEY (`id`)) ENGINE=MyISAM AUTO_INCREMENT=188 DEFAULT CHARSET=utf8;
DROP TABLE IF EXISTS news;
CREATE TABLE `news` (  `id` int(5) NOT NULL AUTO_INCREMENT,  `lm1` int(5) NOT NULL,  `lm2` int(5) NOT NULL,  `lm3` int(5) NOT NULL,  `title` varchar(50) NOT NULL,  `content` text NOT NULL,  `time` varchar(50) NOT NULL,  `hit` int(10) NOT NULL DEFAULT '0',  `adduser` varchar(50) NOT NULL,  `count` int(50) NOT NULL,  PRIMARY KEY (`id`)) ENGINE=MyISAM AUTO_INCREMENT=101 DEFAULT CHARSET=utf8;
DROP TABLE IF EXISTS replay;
CREATE TABLE `replay` (  `id` int(5) NOT NULL AUTO_INCREMENT,  `time` varchar(50) NOT NULL,  `text` text NOT NULL,  `userid` int(5) NOT NULL,  PRIMARY KEY (`id`)) ENGINE=MyISAM AUTO_INCREMENT=97 DEFAULT CHARSET=utf8;
DROP TABLE IF EXISTS user;
CREATE TABLE `user` (  `id` int(5) NOT NULL AUTO_INCREMENT,  `name` varchar(20) NOT NULL,  `time` varchar(50) NOT NULL,  `text` text NOT NULL,  `tx` varchar(50) NOT NULL,  `pf` varchar(50) NOT NULL,  `simi` tinyint(1) NOT NULL,  PRIMARY KEY (`id`)) ENGINE=MyISAM AUTO_INCREMENT=189 DEFAULT CHARSET=utf8;
